int lfs_fs_mkconsistent(lfs_t *lfs) {
    /* Minimal forward declarations to allow logging without including headers here. */
    typedef struct FILE FILE;
    extern FILE *fopen(const char *filename, const char *mode);
    extern int fprintf(FILE *stream, const char *format, ...);
    extern int fflush(FILE *stream);
    extern int fclose(FILE *stream);

    FILE *log = fopen("/tmp/logs/406.txt", "a");

    int err = LFS_LOCK(lfs->cfg);

    if (log) {
        /* Log entry and initial lock result so callers can observe the (un)reachability of the err branch. */
        (void) fprintf(log, "ENTER: lfs_fs_mkconsistent: lfs=%p, cfg=%p, initial_err=%d\n",
                       (void *)lfs, (void *)lfs->cfg, err);
        (void) fflush(log);
    }

    if (err) {
        if (log) {
            (void) fprintf(log, "BRANCH TAKEN: err != 0 -> returning %d\n", err);
            (void) fflush(log);
            (void) fclose(log);
        }
        return err;
    }

    if (log) {
        (void) fprintf(log, "LOCK SUCCEEDED: proceeding to lfs_fs_mkconsistent_()\n");
        (void) fflush(log);
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);

    err = lfs_fs_mkconsistent_(lfs);

    if (log) {
        (void) fprintf(log, "AFTER lfs_fs_mkconsistent_: returned %d\n", err);
        (void) fflush(log);
    }

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        (void) fprintf(log, "UNLOCK CALLED: returning %d\n", err);
        (void) fclose(log);
    }

    return err;
}
