int lfs_fs_mkconsistent(lfs_t *lfs) {
    /* Probe logging to capture control flow and state for Rule 2.2 investigation.
       Logs appended to /tmp/logs/408.txt so external testers can inspect whether
       LFS_LOCK/LFS_UNLOCK are no-ops and whether the error branch is ever taken. */
    FILE *fp = fopen("/tmp/logs/408.txt", "a");
    if (fp) {
        fprintf(fp, "ENTER lfs_fs_mkconsistent: lfs=%p, cfg=%p\n", (void*)lfs, (void*)(lfs ? lfs->cfg : NULL));
        fflush(fp);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (fp) {
        fprintf(fp, "LFS_LOCK returned %d (cfg=%p)\n", err, (void*)lfs->cfg);
        fflush(fp);
    }

    if (err) {
        if (fp) {
            fprintf(fp, "EARLY RETURN: lock error %d -> returning without unlocking\n", err);
            fclose(fp);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);

    err = lfs_fs_mkconsistent_(lfs);

    if (fp) {
        fprintf(fp, "lfs_fs_mkconsistent_ returned %d\n", err);
        fflush(fp);
    }

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);

    if (fp) {
        fprintf(fp, "About to call LFS_UNLOCK(cfg=%p)\n", (void*)lfs->cfg);
        fflush(fp);
    }

    LFS_UNLOCK(lfs->cfg);

    if (fp) {
        fprintf(fp, "Called LFS_UNLOCK(cfg=%p)\n", (void*)lfs->cfg);
        fprintf(fp, "EXIT lfs_fs_mkconsistent -> %d\n", err);
        fclose(fp);
    }

    return err;
}
