int lfs_fs_mkconsistent(lfs_t *lfs) {
    int err = LFS_LOCK(lfs->cfg);

    /* Attempt to log control-flow and state to /tmp/logs/408.txt. */
    FILE *logf = fopen("/tmp/logs/408.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_fs_mkconsistent: lfs=%p, lock_err=%d\n", (void*)lfs, err);
        fflush(logf);
    } else {
        /* If logfile can't be opened, emit a trace so behavior is still observable. */
        LFS_TRACE("lfs_fs_mkconsistent: failed to open /tmp/logs/408.txt");
    }

    if (err) {
        if (logf) {
            fprintf(logf, "LOCK failed, returning %d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);
    if (logf) {
        fprintf(logf, "LOCK succeeded, calling lfs_fs_mkconsistent_()\n");
        fflush(logf);
    }

    err = lfs_fs_mkconsistent_(lfs);

    if (logf) {
        fprintf(logf, "lfs_fs_mkconsistent_ returned %d\n", err);
    }

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "UNLOCK called, returning %d\n", err);
        fclose(logf);
    }

    return err;
}
