static lfs_stag_t lfs_fs_parent(lfs_t *lfs, const lfs_block_t pair[2],
        lfs_mdir_t *parent) {
    /* Logging probe: append structured logs to /tmp/logs/615.txt to help
       reproduce the scenario where a non-const compound literal address is
       passed as callback data. This intentionally only reads state and does
       not alter program behavior. */
    FILE *logf = fopen("/tmp/logs/615.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_fs_parent: lfs=%p pair=[%llu,%llu] parent=%p\n",
                (void *)lfs,
                (unsigned long long)pair[0], (unsigned long long)pair[1],
                (void *)parent);
        fclose(logf);
    }

    // use fetchmatch with callback to find pairs
    parent->tail[0] = 0;
    parent->tail[1] = 1;
    struct lfs_tortoise_t tortoise = {
        .pair = {LFS_BLOCK_NULL, LFS_BLOCK_NULL},
        .i = 1,
        .period = 1,
    };
    int err = LFS_ERR_OK;
    int loop_count = 0;
    while (!lfs_pair_isnull(parent->tail)) {
        loop_count++;
        /* Log tortoise and parent tail before detection */
        logf = fopen("/tmp/logs/615.txt", "a");
        if (logf) {
            fprintf(logf, "ITER %d: parent->tail=[%llu,%llu] tortoise.pair=[%llu,%llu] tortoise.i=%d tortoise.period=%d\n",
                    loop_count,
                    (unsigned long long)parent->tail[0], (unsigned long long)parent->tail[1],
                    (unsigned long long)tortoise.pair[0], (unsigned long long)tortoise.pair[1],
                    (int)tortoise.i, (int)tortoise.period);
            fclose(logf);
        }

        err = lfs_tortoise_detectcycles(parent, &tortoise);
        if (err < 0) {
            logf = fopen("/tmp/logs/615.txt", "a");
            if (logf) {
                fprintf(logf, "ITER %d: lfs_tortoise_detectcycles returned err=%d -> RETURN\n",
                        loop_count, err);
                fclose(logf);
            }
            return err;
        }

        /* Prepare the (non-const) compound literal that is passed as callback
           data in the original code. We log the address and contents so that
           reproduction agents can see the pointer value and confirm it's an
           address of a compound literal whose contents are only read by the
           callback. */
        struct lfs_fs_parent_match _match_data = {
            lfs, {pair[0], pair[1]}
        };
        /* Log the compound-literal-like data address and contents */
        logf = fopen("/tmp/logs/615.txt", "a");
        if (logf) {
            fprintf(logf, "ITER %d: prepared match_data at %p -> lfs=%p pair=[%llu,%llu]\n",
                    loop_count, (void *)&_match_data, (void *)_match_data.lfs,
                    (unsigned long long)_match_data.pair[0], (unsigned long long)_match_data.pair[1]);
            fclose(logf);
        }

        lfs_stag_t tag = lfs_dir_fetchmatch(lfs, parent, parent->tail,
                LFS_MKTAG(0x7ff, 0, 0x3ff),
                LFS_MKTAG(LFS_TYPE_DIRSTRUCT, 0, 8),
                NULL,
                lfs_fs_parent_match, &_match_data);

        /* Log the result of fetchmatch and any tag value */
        logf = fopen("/tmp/logs/615.txt", "a");
        if (logf) {
            fprintf(logf, "ITER %d: lfs_dir_fetchmatch returned tag=%lld\n",
                    loop_count, (long long)tag);
            fclose(logf);
        }

        if (tag && tag != LFS_ERR_NOENT) {
            logf = fopen("/tmp/logs/615.txt", "a");
            if (logf) {
                fprintf(logf, "ITER %d: returning tag=%lld (non-zero and not NOENT)\n",
                        loop_count, (long long)tag);
                fclose(logf);
            }
            return tag;
        }
    }

    logf = fopen("/tmp/logs/615.txt", "a");
    if (logf) {
        fprintf(logf, "EXIT lfs_fs_parent: reached end -> RETURN LFS_ERR_NOENT (%d)\n", LFS_ERR_NOENT);
        fclose(logf);
    }

    return LFS_ERR_NOENT;
}
