static lfs_stag_t lfs_fs_parent(lfs_t *lfs, const lfs_block_t pair[2],
        lfs_mdir_t *parent) {
    /* Minimal forward declarations for stdio to avoid adding headers. */
    typedef struct FILE FILE;
    extern FILE *fopen(const char *filename, const char *mode);
    extern int fprintf(FILE *stream, const char *format, ...);
    extern int fclose(FILE *stream);

    FILE *log = fopen("/tmp/logs/615.txt", "a");
    if (log) {
        (void)fprintf(log, "ENTER lfs_fs_parent: lfs=%p parent=%p pair=(%llu,%llu)\n",
            (void *)lfs, (void *)parent,
            (unsigned long long)pair[0], (unsigned long long)pair[1]);
    }

    /* use fetchmatch with callback to find pairs */
    parent->tail[0] = 0;
    parent->tail[1] = 1;
    if (log) {
        (void)fprintf(log, "  init parent->tail=(%llu,%llu)\n",
            (unsigned long long)parent->tail[0], (unsigned long long)parent->tail[1]);
    }

    struct lfs_tortoise_t tortoise = {
        .pair = {LFS_BLOCK_NULL, LFS_BLOCK_NULL},
        .i = 1,
        .period = 1,
    };
    if (log) {
        (void)fprintf(log, "  init tortoise.pair=(%llu,%llu) i=%d period=%d\n",
            (unsigned long long)tortoise.pair[0], (unsigned long long)tortoise.pair[1],
            (int)tortoise.i, (int)tortoise.period);
    }

    int err = LFS_ERR_OK;
    while (!lfs_pair_isnull(parent->tail)) {
        if (log) {
            (void)fprintf(log, "LOOP: parent->tail=(%llu,%llu) tortoise.i=%d tortoise.period=%d\n",
                (unsigned long long)parent->tail[0], (unsigned long long)parent->tail[1],
                (int)tortoise.i, (int)tortoise.period);
        }

        err = lfs_tortoise_detectcycles(parent, &tortoise);
        if (log) {
            (void)fprintf(log, "  lfs_tortoise_detectcycles returned err=%d\n", err);
        }
        if (err < 0) {
            if (log) {
                (void)fprintf(log, "EXIT lfs_fs_parent early (tortoise error) err=%d\n", err);
                (void)fclose(log);
            }
            return err;
        }

        /* Prepare the callback data and log it. Use a local variable instead of
           a compound literal so we can log its address and contents. This keeps
           behavior equivalent while providing evidence about the pointer passed
           to the callback (the scenario under inspection). */
        struct lfs_fs_parent_match find = { lfs, { pair[0], pair[1] } };
        if (log) {
            (void)fprintf(log, "  prepared callback data: &find=%p find.lfs=%p find.pair=(%llu,%llu)\n",
                (void *)&find, (void *)find.lfs,
                (unsigned long long)find.pair[0], (unsigned long long)find.pair[1]);
        }

        lfs_stag_t tag = lfs_dir_fetchmatch(lfs, parent, parent->tail,
                LFS_MKTAG(0x7ff, 0, 0x3ff),
                LFS_MKTAG(LFS_TYPE_DIRSTRUCT, 0, 8),
                NULL,
                lfs_fs_parent_match, &find);

        if (log) {
            (void)fprintf(log, "  lfs_dir_fetchmatch returned tag=%lld\n", (long long)tag);
        }

        if (tag && tag != LFS_ERR_NOENT) {
            if (log) {
                (void)fprintf(log, "EXIT lfs_fs_parent returning tag=%lld\n", (long long)tag);
                (void)fclose(log);
            }
            return tag;
        }
    }

    if (log) {
        (void)fprintf(log, "EXIT lfs_fs_parent returning LFS_ERR_NOENT=%d\n", LFS_ERR_NOENT);
        (void)fclose(log);
    }

    return LFS_ERR_NOENT;
}
