static int lfs_fs_parent_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    struct lfs_fs_parent_match *find = data;
    lfs_t *lfs = find->lfs;
    const struct lfs_diskoff *disk = buffer;
    (void)tag;

    /* Minimal probe: log entry and key parameters to aid reproduction and debugging.
       Appending so multiple runs accumulate evidence. */
    {
        FILE *fp = fopen("/tmp/logs/202.txt", "a");
        if (fp) {
            fprintf(fp, "ENTER lfs_fs_parent_match: this=%p find=%p lfs=%p\n",
                    (void*)data, (void*)find, (void*)lfs);
            fprintf(fp, "  disk->block=%lu disk->off=%lu block_size=%lu sizeof_child=%zu\n",
                    (unsigned long)disk->block,
                    (unsigned long)disk->off,
                    (unsigned long)lfs->cfg->block_size,
                    sizeof(lfs_block_t[2]));
            fclose(fp);
        }
    }

    lfs_block_t child[2];
    int err = lfs_bd_read(lfs,
            &lfs->pcache, &lfs->rcache, lfs->cfg->block_size,
            disk->block, disk->off, &child, sizeof(child));

    /* Log the result of the block read and the raw child contents to capture
       the state observed by this function (helps reproduce calls and any
       downstream modifications inside lfs_bd_read). */
    {
        FILE *fp = fopen("/tmp/logs/202.txt", "a");
        if (fp) {
            fprintf(fp, "lfs_bd_read returned err=%d\n", err);
            if (!err) {
                /* Print the two raw values read (they are in little-endian
                   order until lfs_pair_fromle32 is called). */
                fprintf(fp, "  child_raw[0]=%lu child_raw[1]=%lu\n",
                        (unsigned long)child[0], (unsigned long)child[1]);
            }
            fclose(fp);
        }
    }

    if (err) {
        return err;
    }

    /* Convert and compare as original implementation. Log converted values
       and comparison outcome for full traceability. */
    lfs_pair_fromle32(child);

    int cmp_res = (lfs_pair_cmp(child, find->pair) == 0) ? LFS_CMP_EQ : LFS_CMP_LT;

    {
        FILE *fp = fopen("/tmp/logs/202.txt", "a");
        if (fp) {
            fprintf(fp, "after lfs_pair_fromle32: child[0]=%lu child[1]=%lu\n",
                    (unsigned long)child[0], (unsigned long)child[1]);
            fprintf(fp, "find->pair[0]=%lu find->pair[1]=%lu cmp_res=%d\n",
                    (unsigned long)find->pair[0], (unsigned long)find->pair[1], cmp_res);
            fclose(fp);
        }
    }

    return cmp_res;
}
