static int lfs_fs_parent_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    struct lfs_fs_parent_match *find = data;
    lfs_t *lfs = find->lfs;
    const struct lfs_diskoff *disk = buffer;
    (void)tag;

    lfs_block_t child[2];

    /* Open log for appending; best-effort logging, do not affect return value */
    FILE *log = fopen("/tmp/logs/202.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_fs_parent_match: this=%p find=%p lfs=%p disk=%p\n",
            (void*)data, (void*)find, (void*)lfs, (void*)disk);
        fprintf(log, "  read params: block=%llu off=%llu block_size=%llu buffer=%p\n",
            (unsigned long long)disk->block,
            (unsigned long long)disk->off,
            (unsigned long long)lfs->cfg->block_size,
            buffer);
        fflush(log);
    }

    int err = lfs_bd_read(lfs,
            &lfs->pcache, &lfs->rcache, lfs->cfg->block_size,
            disk->block, disk->off, &child, sizeof(child));

    if (log) {
        fprintf(log, "AFTER lfs_bd_read: err=%d raw_child[0]=%llu raw_child[1]=%llu\n",
            err, (unsigned long long)child[0], (unsigned long long)child[1]);
        fflush(log);
    }

    if (err) {
        if (log) {
            fprintf(log, "EXIT lfs_fs_parent_match: returning err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    /* Preserve original behavior: convert to host order then compare */
    lfs_pair_fromle32(child);

    int cmpres = (lfs_pair_cmp(child, find->pair) == 0) ? LFS_CMP_EQ : LFS_CMP_LT;

    if (log) {
        fprintf(log, "AFTER lfs_pair_fromle32: child[0]=%llu child[1]=%llu cmpres=%d\n",
            (unsigned long long)child[0], (unsigned long long)child[1], cmpres);
        fprintf(log, "EXIT lfs_fs_parent_match: returning %d\n", cmpres);
        fclose(log);
    }

    return cmpres;
}
