static int lfs_fs_parent_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    struct lfs_fs_parent_match *find = data;
    lfs_t *lfs = find->lfs;
    const struct lfs_diskoff *disk = buffer;
    (void)tag; /* original no-op preserved (silence unused-parameter) */

    /* Minimal logging to /tmp/logs/208.txt to capture control flow and state.
       Logs are best-effort: if fopen fails we continue without logging so behavior
       is unchanged. */
    {
        FILE *log = fopen("/tmp/logs/208.txt", "a");
        if (log) {
            /* Print entry and key state. Casts to unsigned long used for portability
               across likely integer typedefs (e.g., uint32_t). */
            fprintf(log, "ENTER lfs_fs_parent_match: data=%p tag=%lu buffer=%p\n",
                    data, (unsigned long)tag, buffer);
            fprintf(log, "  disk: block=%lu off=%lu\n",
                    (unsigned long)disk->block, (unsigned long)disk->off);
            fprintf(log, "  find: pair_address=%p\n", (void *)&find->pair);
            fclose(log);
        }
    }

    lfs_block_t child[2];
    int err = lfs_bd_read(lfs,
            &lfs->pcache, &lfs->rcache, lfs->cfg->block_size,
            disk->block, disk->off, &child, sizeof(child));

    /* Log read result */
    {
        FILE *log = fopen("/tmp/logs/208.txt", "a");
        if (log) {
            fprintf(log, "lfs_bd_read returned err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        return err;
    }

    lfs_pair_fromle32(child);

    /* Evaluate comparison and log child contents and comparison result. */
    int cmp = (lfs_pair_cmp(child, find->pair) == 0) ? LFS_CMP_EQ : LFS_CMP_LT;
    {
        FILE *log = fopen("/tmp/logs/208.txt", "a");
        if (log) {
            fprintf(log, "child[0]=%lu child[1]=%lu cmp=%d (returning %d)\n",
                    (unsigned long)child[0], (unsigned long)child[1],
                    (lfs_pair_cmp(child, find->pair) == 0), cmp);
            fclose(log);
        }
    }

    return cmp;
}
