static int lfs_fs_parent_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    /* Local includes to ensure logging functions/types are available
       while keeping this function self-contained as requested. */
    #include <stdio.h>
    #include <stdint.h>
    #include <stddef.h>

    struct lfs_fs_parent_match *find = data;
    lfs_t *lfs = find->lfs;
    const struct lfs_diskoff *disk = buffer;
    (void)tag;

    /* Open log file in append mode so multiple calls accumulate evidence. */
    FILE *logf = fopen("/tmp/logs/205.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER %s: this=%p, data=%p, find=%p, lfs=%p, buffer=%p, disk=%p, tag=%llu\n",
                __func__, (void *)&lfs_fs_parent_match, data, (void*)find, (void*)lfs, buffer, (void*)disk,
                (unsigned long long)tag);
        if (disk) {
            fprintf(logf, " disk->block=%llu, disk->off=%llu\n",
                    (unsigned long long)disk->block, (unsigned long long)disk->off);
        } else {
            fprintf(logf, " disk is NULL\n");
        }
    }

    lfs_block_t child[2];
    int err = lfs_bd_read(lfs,
            &lfs->pcache, &lfs->rcache, lfs->cfg->block_size,
            disk->block, disk->off, &child, sizeof(child));

    if (logf) {
        fprintf(logf, "lfs_bd_read returned err=%d\n", err);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "EXIT %s: returning err=%d\n", __func__, err);
            fclose(logf);
        }
        return err;
    }

    /* Dump raw bytes of the 'child' pair before endianness conversion. */
    if (logf) {
        const uint8_t *b = (const uint8_t *)child;
        fprintf(logf, "child raw (pre-le32) %zu bytes:", (size_t)sizeof(child));
        for (size_t i = 0; i < sizeof(child); i++) {
            fprintf(logf, " %02x", (unsigned)b[i]);
        }
        fprintf(logf, "\n");
    }

    lfs_pair_fromle32(child);

    /* Dump raw bytes after conversion and also the stored pair pointed by find. */
    if (logf) {
        const uint8_t *b = (const uint8_t *)child;
        fprintf(logf, "child raw (post-le32) %zu bytes:", (size_t)sizeof(child));
        for (size_t i = 0; i < sizeof(child); i++) {
            fprintf(logf, " %02x", (unsigned)b[i]);
        }
        fprintf(logf, "\n");

        /* Attempt to log the bytes of find->pair; size is inferred from lfs_pair_cmp usage
           (we treat it as same size as child). */
        if (find && find->pair) {
            const uint8_t *p = (const uint8_t *)find->pair;
            fprintf(logf, "find->pair addr=%p bytes:", (void *)find->pair);
            for (size_t i = 0; i < sizeof(child); i++) {
                fprintf(logf, " %02x", (unsigned)p[i]);
            }
            fprintf(logf, "\n");
        } else {
            fprintf(logf, "find->pair is NULL or find is NULL\n");
        }
    }

    int cmp_res = (lfs_pair_cmp(child, find->pair) == 0) ? LFS_CMP_EQ : LFS_CMP_LT;

    if (logf) {
        fprintf(logf, "lfs_pair_cmp result -> returning %d\n", cmp_res);
        fclose(logf);
    }

    return cmp_res;
}
