lfs_ssize_t lfs_fs_size(lfs_t *lfs) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and the result of the lock to a persistent file for evidence
       of the no-op lock (i.e. err == 0 when LFS_LOCK expands to ((void)cfg,0)). */
    {
        FILE *log = fopen("/tmp/logs/808.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_fs_size: lfs=%p\n", (void*)lfs);
            fprintf(log, "LFS_LOCK returned err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Log early return due to non-zero err */
        FILE *log = fopen("/tmp/logs/808.txt", "a");
        if (log) {
            fprintf(log, "lfs_fs_size: early return due to err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_size(%p)", (void*)lfs);

    lfs_ssize_t res = lfs_fs_size_(lfs);

    /* Log result of the underlying size computation */
    {
        FILE *log = fopen("/tmp/logs/808.txt", "a");
        if (log) {
            fprintf(log, "lfs_fs_size_: res=%" PRId32 "\n", res);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_fs_size -> %" PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    /* Log unlock and final return value */
    {
        FILE *log = fopen("/tmp/logs/808.txt", "a");
        if (log) {
            fprintf(log, "LFS_UNLOCK called for cfg=%p, returning res=%" PRId32 "\n",
                    (void*)lfs->cfg, res);
            fclose(log);
        }
    }

    return res;
}
