lfs_ssize_t lfs_fs_size(lfs_t *lfs) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for analysis */
    FILE *logf = fopen("/tmp/logs/808.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_fs_size: lfs=%p cfg=%p\n", (void*)lfs, (void*)lfs->cfg);
        fprintf(logf, "after LFS_LOCK: err=%d\n", err);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "lfs_fs_size: returning early due to lock error: %d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_size(%p)", (void*)lfs);

    lfs_ssize_t res = lfs_fs_size_(lfs);

    if (logf) {
        fprintf(logf, "lfs_fs_size_: res=%" PRId32 "\n", res);
    }

    LFS_TRACE("lfs_fs_size -> %"PRId32, res);

    /* Log just before unlocking to capture cfg state and demonstrate the call site
       (in some build configurations LFS_UNLOCK compiles to a no-op). */
    if (logf) {
        fprintf(logf, "about to call LFS_UNLOCK(cfg=%p)\n", (void*)lfs->cfg);
    }

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "after LFS_UNLOCK\n");
        fclose(logf);
    }

    return res;
}
