int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    /* Preserve original behavior: attempt to lock using the existing macro */
    int err = LFS_LOCK(lfs->cfg);

    /* Probe logging: append control flow and state information to the log file.
       This captures the value of "err" right after the lock, which is the
       critical piece of evidence for the reported Rule 14.3 violation
       (e.g. LFS_LOCK(cfg) may expand to ((void)cfg,0), leaving err==0). */
    FILE *log = fopen("/tmp/logs/669.txt", "a");
    if (log) {
        fprintf(log, "lfs_fs_stat ENTER: lfs=%p, fsinfo=%p, err(after LFS_LOCK)=%d\n",
                (void*)lfs, (void*)fsinfo, err);
        fclose(log);
    }

    /* Original conditional: if err is non-zero return. We also log which path
       is taken so test runs produce clear evidence whether the controlling
       expression was effectively invariant (always false) in practice. */
    if (err) {
        FILE *log2 = fopen("/tmp/logs/669.txt", "a");
        if (log2) {
            fprintf(log2, "lfs_fs_stat: branch if(err) taken -> returning %d\n", err);
            fclose(log2);
        }
        return err;
    }

    /* Preserve original tracing and call into internal function. */
    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);

    /* Log the result of the internal call. */
    FILE *log3 = fopen("/tmp/logs/669.txt", "a");
    if (log3) {
        fprintf(log3, "lfs_fs_stat: called lfs_fs_stat_ -> err=%d\n", err);
        fclose(log3);
    }

    LFS_TRACE("lfs_fs_stat -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    FILE *log4 = fopen("/tmp/logs/669.txt", "a");
    if (log4) {
        fprintf(log4, "lfs_fs_stat EXIT: returning %d\n", err);
        fclose(log4);
    }

    return err;
}
