int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe log: record entry and lock result. */
    {
        FILE *__logf = fopen("/tmp/logs/666.txt", "a");
        if (__logf) {
            fprintf(__logf,
                "lfs_fs_stat: entry lfs=%p fsinfo=%p cfg=%p LFS_LOCK_return=%d\n",
                (void*)lfs, (void*)fsinfo, (void*)lfs->cfg, err);
            fflush(__logf);
            fclose(__logf);
        }
    }

    if (err) {
        /* Probe log for early return branch (should be unreachable under the
           reported configuration where LFS_LOCK is defined as ((void)cfg,0)). */
        FILE *__logf = fopen("/tmp/logs/666.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_fs_stat: early return due to lock error err=%d\n", err);
            fflush(__logf);
            fclose(__logf);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    /* Probe log before calling the underlying implementation. */
    {
        FILE *__logf = fopen("/tmp/logs/666.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_fs_stat: calling lfs_fs_stat_ (locked), cfg=%p\n", (void*)lfs->cfg);
            fflush(__logf);
            fclose(__logf);
        }
    }

    err = lfs_fs_stat_(lfs, fsinfo);

    LFS_TRACE("lfs_fs_stat -> %d", err);

    LFS_UNLOCK(lfs->cfg);

    /* Probe log for result and unlock. */
    {
        FILE *__logf = fopen("/tmp/logs/666.txt", "a");
        if (__logf) {
            fprintf(__logf, "lfs_fs_stat: returned %d (after unlock)\n", err);
            fflush(__logf);
            fclose(__logf);
        }
    }

    return err;
}
