int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for reproduction. */
    {
        FILE *fp = fopen("/tmp/logs/668.txt", "a");
        if (fp) {
            fprintf(fp, "ENTER lfs_fs_stat: lfs=%p fsinfo=%p\n", (void*)lfs, (void*)fsinfo);
            fprintf(fp, "After LFS_LOCK: err=%d cfg=%p\n", err, (void*)lfs->cfg);
            fclose(fp);
        }
    }

    if (err) {
        /* Log the early return caused by lock failure. */
        FILE *fp = fopen("/tmp/logs/668.txt", "a");
        if (fp) {
            fprintf(fp, "lfs_fs_stat: returning early due to LFS_LOCK error = %d\n", err);
            fclose(fp);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);

    LFS_TRACE("lfs_fs_stat -> %d", err);

    /* Log just before calling LFS_UNLOCK to capture that the call is executed. */
    {
        FILE *fp = fopen("/tmp/logs/668.txt", "a");
        if (fp) {
            fprintf(fp, "About to call LFS_UNLOCK(lfs->cfg) with cfg=%p\n", (void*)lfs->cfg);
            /* Describe the expected expansion so observers know what to look for. */
            fprintf(fp, "Note: LFS_UNLOCK(cfg) expands to ((void)cfg) which is an executed no-op expression.\n");
            fprintf(fp, "Recorded state before unlock: err=%d lfs=%p fsinfo=%p\n", err, (void*)lfs, (void*)fsinfo);
            fclose(fp);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Log after unlock to show the code path continued. */
    {
        FILE *fp = fopen("/tmp/logs/668.txt", "a");
        if (fp) {
            fprintf(fp, "After LFS_UNLOCK: continued execution, returning err=%d\n", err);
            fclose(fp);
        }
    }

    return err;
}
