int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe log: write control-flow/state information to a file for repro */
    {
        FILE *f = fopen("/tmp/logs/668.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_fs_stat: lfs=%p, fsinfo=%p, cfg=%p, lock_err=%d\n",
                    (void*)lfs, (void*)fsinfo, (void*)lfs->cfg, err);
            fclose(f);
        }
    }

    if (err) {
        /* Log early return due to lock failure */
        FILE *f = fopen("/tmp/logs/668.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_stat: LFS_LOCK failed, returning %d\n", err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);

    LFS_TRACE("lfs_fs_stat -> %d", err);

    /* Log before calling unlock to capture the state that will (not) change */
    {
        FILE *f = fopen("/tmp/logs/668.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_stat: before LFS_UNLOCK: err=%d, cfg=%p\n", err, (void*)lfs->cfg);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Log after unlock to provide evidence that the unlock was a no-op in this build */
    {
        FILE *f = fopen("/tmp/logs/668.txt", "a");
        if (f) {
            fprintf(f, "lfs_fs_stat: after LFS_UNLOCK: cfg=%p (no observable change recorded)\n",
                    (void*)lfs->cfg);
            fclose(f);
        }
    }

    return err;
}
