int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log lock failure path (if any) so callers can see we returned early */
    if (err) {
        FILE *logf = fopen("/tmp/logs/668.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_fs_stat: LFS_LOCK failed -> err=%d, lfs=%p, fsinfo=%p, cfg=%p\n",
                    err, (void *)lfs, (void *)fsinfo, (void *)(lfs ? lfs->cfg : NULL));
            fclose(logf);
        }
        return err;
    }

    /* Primary trace kept as in original */
    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    /* Entry probe: capture control path and basic state to file for repro/evidence */
    {
        FILE *logf = fopen("/tmp/logs/668.txt", "a");
        if (logf) {
            fprintf(logf, "ENTER lfs_fs_stat: lfs=%p, fsinfo=%p, cfg=%p\n",
                    (void *)lfs, (void *)fsinfo, (void *)(lfs ? lfs->cfg : NULL));
            /* Provide a hint for investigators about where the suspected dead-code was reported. */
            fprintf(logf, "NOTE: external analysis reported a dead-code instance in lfs_fs_disk_version (unused cast '(void)lfs;').\n");
            /* Record a timestamp-like marker (not strict time to avoid needing time.h) by printing an address of a stack variable. */
            {
                int marker_var = 0;
                fprintf(logf, "STACK_MARKER_ADDR=%p\n", (void *)&marker_var);
            }
            fclose(logf);
        }
    }

    /* Preserve original behaviour */
    err = lfs_fs_stat_(lfs, fsinfo);

    LFS_TRACE("lfs_fs_stat -> %d", err);

    /* Exit probe: record result and pointers so the sequence leading to the reported issue is visible */
    {
        FILE *logf = fopen("/tmp/logs/668.txt", "a");
        if (logf) {
            fprintf(logf, "EXIT lfs_fs_stat: err=%d, lfs=%p, fsinfo=%p, cfg=%p\n",
                    err, (void *)lfs, (void *)fsinfo, (void *)(lfs ? lfs->cfg : NULL));
            /* If fsinfo pointer is non-NULL, log its pointer value so callers can inspect changes offline */
            if (fsinfo) {
                fprintf(logf, "fsinfo_ptr=%p\n", (void *)fsinfo);
            }
            fclose(logf);
        }
    }

    LFS_UNLOCK(lfs->cfg);
    return err;
}
