static int lfs_fs_stat_(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    /* Probe log file to capture control flow and state for analysis */
    FILE *probe = fopen("/tmp/logs/118.txt", "a");
    if (probe) {
        fprintf(probe, "lfs_fs_stat_: entered lfs=%p fsinfo=%p\n", (void *)lfs, (void *)fsinfo);
    }

    /* if the superblock is up-to-date, we must be on the most recent
       minor version of littlefs */
    int needs_super = lfs_gstate_needssuperblock(&lfs->gstate);
    if (probe) {
        fprintf(probe, "lfs_gstate_needssuperblock -> %d\n", (int)needs_super);
    }

    if (!needs_super) {
        fsinfo->disk_version = lfs_fs_disk_version(lfs);
        if (probe) {
            fprintf(probe, "branch: superblock up-to-date; disk_version=%lld\n",
                    (long long)fsinfo->disk_version);
        }

    // otherwise we need to read the minor version on disk
    } else {
        /* fetch the superblock */
        lfs_mdir_t dir;
        int err = lfs_dir_fetch(lfs, &dir, lfs->root);
        if (probe) {
            fprintf(probe, "lfs_dir_fetch returned %d\n", err);
        }
        if (err) {
            if (probe) {
                fprintf(probe, "lfs_fs_stat_: returning err %d\n", err);
                fclose(probe);
            }
            return err;
        }

        lfs_superblock_t superblock;
        lfs_stag_t tag = lfs_dir_get(lfs, &dir, LFS_MKTAG(0x7ff, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_INLINESTRUCT, 0, sizeof(superblock)),
                &superblock);
        if (probe) {
            fprintf(probe, "lfs_dir_get returned tag %lld\n", (long long)tag);
        }
        if (tag < 0) {
            if (probe) {
                fprintf(probe, "lfs_fs_stat_: returning tag %lld\n", (long long)tag);
                fclose(probe);
            }
            return tag;
        }
        lfs_superblock_fromle32(&superblock);

        /* read the on-disk version */
        fsinfo->disk_version = superblock.version;
        if (probe) {
            fprintf(probe, "read superblock.version=%lld\n", (long long)fsinfo->disk_version);
        }
    }

    /* filesystem geometry */
    fsinfo->block_size = lfs->cfg->block_size;
    fsinfo->block_count = lfs->block_count;
    if (probe) {
        fprintf(probe, "block_size=%lld block_count=%lld\n",
                (long long)fsinfo->block_size, (long long)fsinfo->block_count);
    }

    /* other on-disk configuration, we cache all of these for internal use */
    fsinfo->name_max = lfs->name_max;
    fsinfo->file_max = lfs->file_max;
    fsinfo->attr_max = lfs->attr_max;
    if (probe) {
        fprintf(probe, "name_max=%lld file_max=%lld attr_max=%lld\n",
                (long long)fsinfo->name_max, (long long)fsinfo->file_max,
                (long long)fsinfo->attr_max);
    }

    if (probe) {
        fprintf(probe, "lfs_fs_stat_: returning 0\n");
        fclose(probe);
    }

    return 0;
}
