lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and lock result */
    {
        FILE *f = fopen("/tmp/logs/759.txt", "a");
        if (f) {
            fprintf(f, "ENTRY lfs_getattr: lfs=%p path=\"%s\" type=%" PRIu8 " buffer=%p size=%" PRIu32 "\n",
                    (void*)lfs, (path != NULL) ? path : "(null)", type, buffer, size);
            fprintf(f, "LOCK probe: LFS_LOCK(lfs->cfg) returned err=%d\n", err);
            fclose(f);
        }
    }

    if (err) {
        /* Probe: log early return due to lock failure */
        FILE *f = fopen("/tmp/logs/759.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_getattr: early return err=%d (lock failed)\n", err);
            fclose(f);
        }
        return err;
    }

    /* Original trace call preserved */
    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    /* Call the underlying implementation and capture result */
    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    /* Probe: log result of underlying call */
    {
        FILE *f = fopen("/tmp/logs/759.txt", "a");
        if (f) {
            fprintf(f, "CALL lfs_getattr_: returned res=%" PRId32 "\n", res);
            fclose(f);
        }
    }

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: log unlock and final return */
    {
        FILE *f = fopen("/tmp/logs/759.txt", "a");
        if (f) {
            fprintf(f, "UNLOCK called: LFS_UNLOCK(lfs->cfg) executed; returning res=%" PRId32 "\n", res);
            fclose(f);
        }
    }

    return res;
}
