lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    /* Preserve original behavior: acquire lock and check for error */
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and the result of LFS_LOCK so callers can reproduce the scenario */
    {
        FILE *logf = fopen("/tmp/logs/759.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_getattr ENTER: lfs=%p, path=%s, type=%u, buffer=%p, size=%llu\n",
                    (void*)lfs, (path ? path : "(null)"), (unsigned)type,
                    buffer, (unsigned long long)size);
            fprintf(logf, "lfs_getattr: after LFS_LOCK -> err=%d\n", err);
            fflush(logf);
            fclose(logf);
        }
    }

    if (err) {
        /* Log the early return so the invariant controlling-expression case is visible */
        FILE *logf = fopen("/tmp/logs/759.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_getattr: returning early due to err=%d\n", err);
            fflush(logf);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    /* Log the result before unlocking so the full control path is recorded */
    {
        FILE *logf = fopen("/tmp/logs/759.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_getattr: lfs_getattr_ returned res=%lld\n", (long long)res);
            fflush(logf);
            fclose(logf);
        }
    }

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    /* Final log indicating unlock and return */
    {
        FILE *logf = fopen("/tmp/logs/759.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_getattr: LFS_UNLOCK called, returning res=%lld\n", (long long)res);
            fflush(logf);
            fclose(logf);
        }
    }

    return res;
}
