lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    /* Logging probe: record entry state to /tmp/logs/752.txt
       This captures the pointer value, the address of the local parameter
       (to help detect if callees take &path), the string contents, and
       the arguments passed into lfs_getattr_. */
    {
        FILE *f = fopen("/tmp/logs/752.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_getattr: lfs=%p path_ptr=%p path_str=\"%s\" &path=%p type=%"PRIu8" buffer=%p size=%"PRIu32"\n",
                    (void*)lfs,
                    (const void*)path,
                    (path != NULL) ? path : "(null)",
                    (const void*)&path,
                    type,
                    buffer,
                    size);
            /* Dump first up to 128 bytes of the path (hex) to help detect
               any in-place modifications or pointer advances done by
               callees operating on the pointer value. */
            if (path != NULL) {
                size_t i;
                fprintf(f, "  path_hex=");
                for (i = 0; i < 128 && path[i] != '\0'; i++) {
                    fprintf(f, "%02x", (unsigned char)path[i]);
                }
                fprintf(f, "\n");
            }
            fclose(f);
        }
    }

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    /* Log return state so callers can compare before/after. If an inner
       function illegally modifies its own 'path' parameter by taking its
       address, evidence may be found by inspecting logs from the callee
       (if similarly instrumented). This wrapper logs the pointer values
       that it observed before and after the call. */
    {
        FILE *f = fopen("/tmp/logs/752.txt", "a");
        if (f) {
            fprintf(f, "EXIT  lfs_getattr: res=%"PRId32" lfs=%p path_ptr=%p path_str=\"%s\" &path=%p type=%"PRIu8" buffer=%p size=%"PRIu32"\n",
                    res,
                    (void*)lfs,
                    (const void*)path,
                    (path != NULL) ? path : "(null)",
                    (const void*)&path,
                    type,
                    buffer,
                    size);
            /* Repeat hex dump for comparison. */
            if (path != NULL) {
                size_t i;
                fprintf(f, "  path_hex=");
                for (i = 0; i < 128 && path[i] != '\0'; i++) {
                    fprintf(f, "%02x", (unsigned char)path[i]);
                }
                fprintf(f, "\n");
            }
            fclose(f);
        }
    }

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
