lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file to record control flow and state for reproducibility */
    FILE *logf = fopen("/tmp/logs/756.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_getattr: lfs=%p path=\"%s\" type=%"PRIu8" buffer=%p size=%"PRIu32"\n",
                (void*)lfs, (path != NULL) ? path : "(null)", type, buffer, size);
        fprintf(logf, "AFTER LFS_LOCK: err=%d\n", err);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "ERROR BRANCH TAKEN: returning %d\n", err);
            fclose(logf);
        }
        return err;
    }

    if (logf) {
        fprintf(logf, "LOCK OK: continuing into lfs_getattr_\n");
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    if (logf) {
        fprintf(logf, "lfs_getattr_ returned %"PRId32"\n", res);
    }

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "EXIT lfs_getattr: returning %"PRId32"\n", res);
        fclose(logf);
    }

    return res;
}
