int lfs_remove(lfs_t *lfs, const char *path) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }
    LFS_TRACE("lfs_remove(%p, \"%s\")", (void*)lfs, path);

    /* Logging probe: record entry, addresses and path value before call. */
    {
        FILE *logf = fopen("/tmp/logs/422.txt", "a");
        if (logf) {
            fprintf(logf, "ENTER lfs_remove: lfs=%p, path_str=%s\n", (void*)lfs, path ? path : "(null)");
            fprintf(logf, "ENTER lfs_remove: path_value=%p, &path=%p\n", (const void*)path, (void*)&path);
            fclose(logf);
        }
    }

    err = lfs_remove_(lfs, path);

    /* Logging probe: record return value and observe whether the parameter 'path' was modified by the callee. */
    {
        FILE *logf = fopen("/tmp/logs/422.txt", "a");
        if (logf) {
            fprintf(logf, "AFTER lfs_remove_: err=%d\n", err);
            fprintf(logf, "AFTER lfs_remove_: path_value=%p, &path=%p\n", (const void*)path, (void*)&path);
            /* If the callee incorrectly modified the parameter pointer, the path_value printed here will differ
               from the value printed before the call above. This provides evidence for the Rule 17.8 violation scenario. */
            fclose(logf);
        }
    }

    LFS_TRACE("lfs_remove -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
