int lfs_remove(lfs_t *lfs, const char *path) {
    /* Minimal forward declarations so we can log to a file without adding headers. */
    struct FILE;
    extern struct FILE *fopen(const char *filename, const char *mode);
    extern int fprintf(struct FILE *stream, const char *format, ...);
    extern int fclose(struct FILE *stream);

    /* Preserve original locking behaviour */
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file (append) and emit entry + state information */
    struct FILE *fp = fopen("/tmp/logs/428.txt", "a");
    if (fp) {
        /* Log entry, pointers and path for control-flow/state evidence */
        fprintf(fp, "ENTER lfs_remove: this=%p path=\"%s\" cfg=%p\n",
                (void *)lfs, (path != NULL) ? path : "(null)", (void *)lfs->cfg);
        fprintf(fp, "LFS_LOCK returned: %d\n", err);
    }

    if (err) {
        if (fp) {
            fprintf(fp, "EXIT lfs_remove early (lock failed) -> %d\n", err);
            (void)fclose(fp);
        }
        return err;
    }

    /* Keep original trace call to avoid changing behaviour */
    LFS_TRACE("lfs_remove(%p, \"%s\")", (void*)lfs, path);

    if (fp) {
        fprintf(fp, "Calling lfs_remove_ (delegated remove)\n");
    }

    err = lfs_remove_(lfs, path);

    if (fp) {
        fprintf(fp, "lfs_remove_ returned: %d\n", err);
        /* Log intention to call unlock and the cfg pointer value. This helps show
           that LFS_UNLOCK is invoked here; if LFS_UNLOCK is a no-op macro like
           ((void)cfg), the state (cfg pointer) will remain unchanged in subsequent logs. */
        fprintf(fp, "About to call LFS_UNLOCK(lfs->cfg) cfg=%p\n", (void *)lfs->cfg);
    }

    /* The statement under inspection: keep it so behaviour is preserved. */
    LFS_UNLOCK(lfs->cfg);

    if (fp) {
        fprintf(fp, "After LFS_UNLOCK; final return value: %d\n", err);
        (void)fclose(fp);
    }

    LFS_TRACE("lfs_remove -> %d", err);
    return err;
}
