int lfs_remove(lfs_t *lfs, const char *path) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and initial state to help reproduce memcmp string-compare scenario */
    do {
        FILE *log = fopen("/tmp/logs/424.txt", "a");
        if (log) {
            fprintf(log, "[lfs_remove] ENTER: lfs=%p, cfg=%p, path_ptr=%p, path=\"%s\", lock_err=%d\n",
                    (void*)lfs, (void*)lfs->cfg, (const void*)path, path ? path : "(null)", err);
            fclose(log);
        }
    } while (0);

    if (err) {
        /* preserve original early return behavior */
        return err;
    }

    LFS_TRACE("lfs_remove(%p, \"%s\")", (void*)lfs, path);

    /* Probe: log just before delegating to lfs_remove_ */
    do {
        FILE *log = fopen("/tmp/logs/424.txt", "a");
        if (log) {
            fprintf(log, "[lfs_remove] BEFORE lfs_remove_: path_ptr=%p, path=\"%s\"\n",
                    (const void*)path, path ? path : "(null)");
            fclose(log);
        }
    } while (0);

    err = lfs_remove_(lfs, path);

    /* Probe: log result from lfs_remove_ and control flow shown by returned error code */
    do {
        FILE *log = fopen("/tmp/logs/424.txt", "a");
        if (log) {
            fprintf(log, "[lfs_remove] AFTER lfs_remove_: returned err=%d, path_ptr=%p, path=\"%s\"\n",
                    err, (const void*)path, path ? path : "(null)");
            fclose(log);
        }
    } while (0);

    LFS_TRACE("lfs_remove -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
