static int lfs_remove_(lfs_t *lfs, const char *path) {
    FILE *fp = fopen("/tmp/logs/95.txt", "a");
    if (fp) {
        fprintf(fp, "ENTER lfs_remove_: lfs=%p path=%s\n", (void *)lfs, (path ? path : "(null)"));
        fflush(fp);
    }

    // deorphan if we haven't yet, needed at most once after poweron
    int err = lfs_fs_forceconsistency(lfs);
    if (err) {
        if (fp) {
            fprintf(fp, "lfs_remove_: fs_forceconsistency returned err=%d\n", err);
            fclose(fp);
        }
        return err;
    }

    lfs_mdir_t cwd;
    lfs_stag_t tag = lfs_dir_find(lfs, &cwd, &path, NULL);
    if (tag < 0 || lfs_tag_id(tag) == 0x3ff) {
        if (fp) {
            fprintf(fp, "lfs_remove_: lfs_dir_find returned tag=%llu (neg=%d), id=%u -> returning\n",
                    (unsigned long long)tag, (tag < 0), (unsigned)lfs_tag_id(tag));
            fclose(fp);
        }
        return (tag < 0) ? (int)tag : LFS_ERR_INVAL;
    }

    if (fp) {
        fprintf(fp, "lfs_remove_: found tag=%llu type=%u id=%u\n",
                (unsigned long long)tag, (unsigned)lfs_tag_type3(tag), (unsigned)lfs_tag_id(tag));
        fflush(fp);
    }

    struct lfs_mlist dir;
    dir.next = lfs->mlist;
    if (lfs_tag_type3(tag) == LFS_TYPE_DIR) {
        // must be empty before removal
        lfs_block_t pair[2];
        if (fp) {
            fprintf(fp, "lfs_remove_: preparing to lfs_dir_get for directory id=%u\n",
                    (unsigned)lfs_tag_id(tag));
            fflush(fp);
        }
        lfs_stag_t res = lfs_dir_get(lfs, &cwd, LFS_MKTAG(0x700, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_STRUCT, lfs_tag_id(tag), 8), pair);
        if (res < 0) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_dir_get returned res=%lld\n", (long long)res);
                fclose(fp);
            }
            return (int)res;
        }

        if (fp) {
            fprintf(fp, "lfs_remove_: pair raw before le32: pair[0]=%llu pair[1]=%llu addr=%p\n",
                    (unsigned long long)pair[0], (unsigned long long)pair[1], (void *)pair);
            fflush(fp);
        }

        lfs_pair_fromle32(pair);

        if (fp) {
            fprintf(fp, "lfs_remove_: pair after lfs_pair_fromle32: pair[0]=%llu pair[1]=%llu addr=%p\n",
                    (unsigned long long)pair[0], (unsigned long long)pair[1], (void *)pair);
            fflush(fp);
        }

        err = lfs_dir_fetch(lfs, &dir.m, pair);
        if (err) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_dir_fetch returned err=%d\n", err);
                fclose(fp);
            }
            return err;
        }

        if (fp) {
            /* Log the mlist pair that will later be passed to lfs_fs_pred */
            fprintf(fp, "lfs_remove_: after fetch dir.m.pair addr=%p values=[%llu,%llu] count=%u split=%u\n",
                    (void *)dir.m.pair,
                    (unsigned long long)dir.m.pair[0], (unsigned long long)dir.m.pair[1],
                    (unsigned)dir.m.count, (unsigned)dir.m.split);
            fflush(fp);
        }

        if (dir.m.count > 0 || dir.m.split) {
            if (fp) {
                fprintf(fp, "lfs_remove_: directory not empty (count=%u split=%u) -> NOTEMPTY\n",
                        (unsigned)dir.m.count, (unsigned)dir.m.split);
                fclose(fp);
            }
            return LFS_ERR_NOTEMPTY;
        }

        // mark fs as orphaned
        err = lfs_fs_preporphans(lfs, +1);
        if (err) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_fs_preporphans(+1) returned err=%d\n", err);
                fclose(fp);
            }
            return err;
        }

        // I know it's crazy but yes, dir can be changed by our parent's
        // commit (if predecessor is child)
        dir.type = 0;
        dir.id = 0;
        lfs->mlist = &dir;

        if (fp) {
            fprintf(fp, "lfs_remove_: marked orphan and linked dir into mlist (mlist=%p)\n", (void *)lfs->mlist);
            fflush(fp);
        }
    }

    // delete the entry
    err = lfs_dir_commit(lfs, &cwd, LFS_MKATTRS(
            {LFS_MKTAG(LFS_TYPE_DELETE, lfs_tag_id(tag), 0), NULL}));
    if (err) {
        lfs->mlist = dir.next;
        if (fp) {
            fprintf(fp, "lfs_remove_: lfs_dir_commit returned err=%d, restored mlist to %p\n", err, (void *)lfs->mlist);
            fclose(fp);
        }
        return err;
    }

    lfs->mlist = dir.next;
    if (fp) {
        fprintf(fp, "lfs_remove_: commit succeeded, restored mlist to %p\n", (void *)lfs->mlist);
        fflush(fp);
    }
    if (lfs_gstate_hasorphans(&lfs->gstate)) {
        LFS_ASSERT(lfs_tag_type3(tag) == LFS_TYPE_DIR);

        // fix orphan
        err = lfs_fs_preporphans(lfs, -1);
        if (err) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_fs_preporphans(-1) returned err=%d\n", err);
                fclose(fp);
            }
            return err;
        }

        if (fp) {
            fprintf(fp, "lfs_remove_: about to call lfs_fs_pred with dir.m.pair addr=%p values=[%llu,%llu] cwd=%p\n",
                    (void *)dir.m.pair,
                    (unsigned long long)dir.m.pair[0], (unsigned long long)dir.m.pair[1], (void *)&cwd);
            fflush(fp);
        }

        err = lfs_fs_pred(lfs, dir.m.pair, &cwd);
        if (err) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_fs_pred returned err=%d (called with non-const pointer dir.m.pair=%p)\n",
                        err, (void *)dir.m.pair);
                fclose(fp);
            }
            return err;
        }

        if (fp) {
            fprintf(fp, "lfs_remove_: lfs_fs_pred succeeded\n");
            fflush(fp);
        }

        err = lfs_dir_drop(lfs, &cwd, &dir.m);
        if (err) {
            if (fp) {
                fprintf(fp, "lfs_remove_: lfs_dir_drop returned err=%d\n", err);
                fclose(fp);
            }
            return err;
        }

        if (fp) {
            fprintf(fp, "lfs_remove_: lfs_dir_drop succeeded\n");
            fflush(fp);
        }
    }

    if (fp) {
        fprintf(fp, "EXIT lfs_remove_: returning 0\n");
        fclose(fp);
    }

    return 0;
}
