int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and lock result to /tmp/logs/516.txt for reproduction */
    {
        FILE *log = fopen("/tmp/logs/516.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_removeattr: lfs=%p, cfg=%p, path=\"%s\", type=%" PRIu8 "\n",
                    (void*)lfs, (void*)lfs->cfg, (path ? path : "(null)"), type);
            fprintf(log, "AFTER LFS_LOCK: err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Probe: log early return due to lock failure */
        FILE *log = fopen("/tmp/logs/516.txt", "a");
        if (log) {
            fprintf(log, "UNREACHABLE-CHECK: early return from lfs_removeattr due to err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_removeattr(%p, \"%s\", %"PRIu8")", (void*)lfs, path, type);

    err = lfs_removeattr_(lfs, path, type);

    /* Probe: log result of underlying call */
    {
        FILE *log = fopen("/tmp/logs/516.txt", "a");
        if (log) {
            fprintf(log, "AFTER lfs_removeattr_: err=%d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_removeattr -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: log exit and final state */
    {
        FILE *log = fopen("/tmp/logs/516.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_removeattr: returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
