int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    /* Probe log to capture control flow and state for MISRA Rule 2.1 investigation. */
    int err = LFS_LOCK(lfs->cfg);

    /* Try to log entry and the result of LFS_LOCK to a file for reproducible evidence. */
    {
        FILE *f = fopen("/tmp/logs/516.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_removeattr: lfs=%p path=\"%s\" type=%u\n",
                    (void*)lfs, path ? path : "(null)", (unsigned)type);
            fprintf(f, "After LFS_LOCK: err=%d\n", err);
            fclose(f);
        }
    }

    if (err) {
        /* Log the taken branch before returning so the unreachable-return scenario can be observed. */
        FILE *f = fopen("/tmp/logs/516.txt", "a");
        if (f) {
            fprintf(f, "if (err) branch taken: returning %d\n", err);
            fclose(f);
        }
        return err;
    }

    /* Log that execution continues past the lock failure check. */
    {
        FILE *f = fopen("/tmp/logs/516.txt", "a");
        if (f) {
            fprintf(f, "if (err) branch not taken: continuing to lfs_removeattr_\n");
            fclose(f);
        }
    }

    LFS_TRACE("lfs_removeattr(%p, \"%s\", %" PRIu8 ")", (void*)lfs, path, type);

    err = lfs_removeattr_(lfs, path, type);

    /* Log the result of the internal call and the upcoming unlock. */
    {
        FILE *f = fopen("/tmp/logs/516.txt", "a");
        if (f) {
            fprintf(f, "lfs_removeattr_ returned: %d\n", err);
            fprintf(f, "Calling LFS_UNLOCK and returning %d\n", err);
            fclose(f);
        }
    }

    LFS_TRACE("lfs_removeattr -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
