int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    int err;

    /* Minimal entry log to show the function was entered. Do NOT dereference lfs->cfg here
       to avoid changing original evaluation order/behavior (LFS_LOCK will evaluate cfg). */
    {
        FILE *f = fopen("/tmp/logs/518.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_removeattr: lfs=%p path=\"%s\" type=%u\n", (void*)lfs, path ? path : "(null)", (unsigned)type);
            fclose(f);
        }
    }

    /* Call the original lock (may evaluate lfs->cfg as part of the macro expansion). */
    err = LFS_LOCK(lfs->cfg);
    if (err) {
        FILE *f = fopen("/tmp/logs/518.txt", "a");
        if (f) {
            fprintf(f, "LFS_LOCK returned err=%d\n", err);
            fclose(f);
        }
        return err;
    }

    /* After LFS_LOCK is evaluated, log the value read for cfg to provide evidence that
       the cfg sub-expression was evaluated (or at least observed). */
    {
        FILE *f = fopen("/tmp/logs/518.txt", "a");
        if (f) {
            fprintf(f, "LFS_LOCK succeeded; observed cfg=%p\n", (void*)(lfs ? lfs->cfg : NULL));
            fclose(f);
        }
    }

    LFS_TRACE("lfs_removeattr(%p, \"%s\", %u)", (void*)lfs, path ? path : "(null)", (unsigned)type);

    err = lfs_removeattr_(lfs, path, type);

    /* Log the result before unlocking. */
    {
        FILE *f = fopen("/tmp/logs/518.txt", "a");
        if (f) {
            fprintf(f, "lfs_removeattr -> %d\n", err);
            fclose(f);
        }
    }

    LFS_TRACE("lfs_removeattr -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    {
        FILE *f = fopen("/tmp/logs/518.txt", "a");
        if (f) {
            fprintf(f, "LFS_UNLOCK called; returning %d\n", err);
            fclose(f);
        }
    }

    return err;
}
