int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for reproducing the violation scenario. */
    FILE *log = fopen("/tmp/logs/518.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_removeattr: lfs=%p path=\"%s\" type=%u cfg=%p\n",
                (void*)lfs, (path ? path : "(null)"), (unsigned)type, (void*)lfs->cfg);
    }

    if (err) {
        if (log) {
            fprintf(log, "LFS_LOCK returned err=%d -> returning early\n", err);
            fclose(log);
        }
        return err;
    }

    /* Original trace retained */
    LFS_TRACE("lfs_removeattr(%p, \"%s\", %" PRIu8 ")", (void*)lfs, path, type);

    /* Call the underlying implementation and log the result. */
    err = lfs_removeattr_(lfs, path, type);

    if (log) {
        fprintf(log, "lfs_removeattr_ returned %d\n", err);
    }

    LFS_TRACE("lfs_removeattr -> %d", err);

    /* Log state before calling LFS_UNLOCK so we can demonstrate whether it has any effect. */
    if (log) {
        fprintf(log, "Before LFS_UNLOCK cfg=%p\n", (void*)lfs->cfg);
    }

    /* Call the unlock as in original code. In the reported build this macro expands to ((void)cfg),
       which is a no-op. We still call it to preserve behavior. */
    LFS_UNLOCK(lfs->cfg);

    /* Log state after LFS_UNLOCK to show whether anything changed (evidence of no-op). */
    if (log) {
        fprintf(log, "After LFS_UNLOCK cfg=%p (note: LFS_UNLOCK may expand to ((void)cfg) in this build)\n", (void*)lfs->cfg);
        fprintf(log, "EXIT lfs_removeattr -> %d\n", err);
        fclose(log);
    }

    return err;
}
