int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe logging for control flow and state to /tmp/logs/518.txt */
    FILE *lfs_log = NULL;
    lfs_log = fopen("/tmp/logs/518.txt", "a");
    if (lfs_log) {
        fprintf(lfs_log, "lfs_removeattr ENTER: lfs=%p path=\"%s\" type=%u\n",
                (void*)lfs, (path != NULL) ? path : "(null)", (unsigned)type);
        fprintf(lfs_log, "LFS_LOCK returned: %d\n", err);
        fflush(lfs_log);
    }

    if (err) {
        if (lfs_log) {
            fprintf(lfs_log, "LFS_LOCK indicated error, returning %d\n", err);
            fclose(lfs_log);
        }
        return err;
    }

    LFS_TRACE("lfs_removeattr(%p, \"%s\", %"PRIu8")", (void*)lfs, path, type);

    err = lfs_removeattr_(lfs, path, type);

    if (lfs_log) {
        fprintf(lfs_log, "lfs_removeattr_: returned %d\n", err);
        fflush(lfs_log);
    }

    LFS_TRACE("lfs_removeattr -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (lfs_log) {
        fprintf(lfs_log, "LFS_UNLOCK called, returning %d\n", err);
        fclose(lfs_log);
    }

    return err;
}
