int lfs_rename(lfs_t *lfs, const char *oldpath, const char *newpath) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe logging to capture control flow and state for MISRA Rule 2.1 investigation. */
    FILE *logf = NULL;
    logf = fopen("/tmp/logs/46.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_rename: lfs=%p old=%s new=%s\n",
                (void*)lfs,
                (oldpath != NULL) ? oldpath : "(null)",
                (newpath != NULL) ? newpath : "(null)");
        fprintf(logf, "After LFS_LOCK: err=%d\n", err);
        fflush(logf);
    }

    if (err) {
        /* This branch is expected to be unreachable when LFS_LOCK expands to ((void)cfg,0).
           We still log here so test runs can show whether this path is ever taken. */
        if (logf) {
            fprintf(logf, "EARLY RETURN: err != 0 -> %d\n", err);
            fclose(logf);
            logf = NULL;
        }
        return err;
    }

    LFS_TRACE("lfs_rename(%p, \"%s\", \"%s\")", (void*)lfs, oldpath, newpath);

    if (logf) {
        fprintf(logf, "Calling lfs_rename_()\n");
        fflush(logf);
    }

    err = lfs_rename_(lfs, oldpath, newpath);

    if (logf) {
        fprintf(logf, "lfs_rename_ returned: %d\n", err);
        fflush(logf);
    }

    LFS_TRACE("lfs_rename -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "LFS_UNLOCK called, returning %d\n", err);
        fclose(logf);
    }

    return err;
}
