int lfs_rename(lfs_t *lfs, const char *oldpath, const char *newpath) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for appending; best-effort logging, do not change control flow */
    FILE *logf = fopen("/tmp/logs/48.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_rename: lfs=%p oldpath=\"%s\" newpath=\"%s\" cfg=%p\n",
                (void*)lfs, oldpath ? oldpath : "(null)", newpath ? newpath : "(null)", (void*)lfs->cfg);
        fprintf(logf, "After LFS_LOCK: err=%d\n", err);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "LFS_LOCK returned non-zero (%d), returning immediately\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_rename(%p, \"%s\", \"%s\")", (void*)lfs, oldpath, newpath);

    err = lfs_rename_(lfs, oldpath, newpath);

    if (logf) {
        fprintf(logf, "lfs_rename_ returned %d\n", err);
        fflush(logf);
    }

    LFS_TRACE("lfs_rename -> %d", err);

    if (logf) {
        /* Log the cfg pointer before calling LFS_UNLOCK so the file shows that the statement is reached
           and that the cfg value is unchanged by the unlock (demonstrating a no-op in the tested
           configuration). This provides evidence that the LFS_UNLOCK statement executes but
           does not change program state. */
        fprintf(logf, "About to call LFS_UNLOCK(lfs->cfg) with cfg=%p\n", (void*)lfs->cfg);
        fflush(logf);
    }

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "After LFS_UNLOCK: cfg=%p, returning %d\n", (void*)lfs->cfg, err);
        fclose(logf);
    }

    return err;
}
