int lfs_rename(lfs_t *lfs, const char *oldpath, const char *newpath) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file to record entry and control/state (do not dereference path pointers) */
    FILE *logf = fopen("/tmp/logs/43.txt", "a");
    if (logf) {
        /* Log pointer values and lock result only. Avoid printing path contents to prevent any new
           out-of-bounds reads; we rely on pointer addresses and return codes to reproduce control flow. */
        fprintf(logf, "ENTRY: lfs_rename called: lfs=%p oldpath=%p newpath=%p LFS_LOCK_ret=%d\n",
                (void*)lfs, (void*)oldpath, (void*)newpath, err);
        fclose(logf);
    }

    if (err) {
        return err;
    }

    LFS_TRACE("lfs_rename(%p, \"%s\", \"%s\")", (void*)lfs, oldpath, newpath);

    err = lfs_rename_(lfs, oldpath, newpath);

    /* Log the result and some contextual info after calling the underlying implementation. */
    logf = fopen("/tmp/logs/43.txt", "a");
    if (logf) {
        fprintf(logf, "EXIT: lfs_rename finished: lfs=%p oldpath=%p newpath=%p -> err=%d\n",
                (void*)lfs, (void*)oldpath, (void*)newpath, err);
        fclose(logf);
    }

    LFS_TRACE("lfs_rename -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
