int lfs_setattr(lfs_t *lfs, const char *path,
        uint8_t type, const void *buffer, lfs_size_t size) {
    /* Probe: log entry and the state around the lock call to /tmp/logs/86.txt */
    int err = LFS_LOCK(lfs->cfg);

    FILE *___logf = fopen("/tmp/logs/86.txt", "a");
    if (___logf) {
        /* Log function entry and parameters */
        fprintf(___logf, "ENTER lfs_setattr: lfs=%p path=\"%s\" type=%" PRIu8 " buffer=%p size=%" PRIu32 "\n",
                (void*)lfs, (path ? path : "(null)"), type, buffer, size);
        /* Log result of LFS_LOCK and note whether the early-return branch will be taken */
        fprintf(___logf, "after LFS_LOCK(lfs->cfg): err=%d\n", err);
        if (err) {
            fprintf(___logf, "if (err) branch WOULD be taken: returning %d (early return)\n", err);
        }
        else {
            fprintf(___logf, "if (err) branch NOT taken: continuing to lfs_setattr_\n");
        }
        fclose(___logf);
    }

    if (err) {
        return err;
    }

    LFS_TRACE("lfs_setattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    err = lfs_setattr_(lfs, path, type, buffer, size);

    /* Probe: log the return from the internal helper and the unlocking */
    ___logf = fopen("/tmp/logs/86.txt", "a");
    if (___logf) {
        fprintf(___logf, "lfs_setattr_ returned: err=%d\n", err);
        fprintf(___logf, "about to call LFS_UNLOCK(lfs->cfg)\n");
        fclose(___logf);
    }

    LFS_TRACE("lfs_setattr -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Final probe: log final return value */
    ___logf = fopen("/tmp/logs/86.txt", "a");
    if (___logf) {
        fprintf(___logf, "EXIT lfs_setattr: returning %d\n", err);
        fclose(___logf);
    }

    return err;
}
