int lfs_setattr(lfs_t *lfs, const char *path,
        uint8_t type, const void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Probe: append runtime trace information to a reproducible log file. */
    FILE *logf = fopen("/tmp/logs/88.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_setattr: lfs=%p path=\"%s\" type=%" PRIu8 " buffer=%p size=%" PRIu32 "\n",
                (void*)lfs, (path ? path : "(null)"), type, buffer, size);
        /* We log the address of the cfg member to avoid assuming its type. */
        fprintf(logf, "PRE: address of lfs->cfg = %p\n", (void*)&lfs->cfg);
        fflush(logf);
    }

    LFS_TRACE("lfs_setattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    err = lfs_setattr_(lfs, path, type, buffer, size);

    if (logf) {
        fprintf(logf, "lfs_setattr_: returned %d\n", err);
        fprintf(logf, "POST_BEFORE_UNLOCK: address of lfs->cfg = %p\n", (void*)&lfs->cfg);
        fflush(logf);
    }

    LFS_TRACE("lfs_setattr -> %d", err);

    /* Call the unlock macro as in original code to preserve behaviour. */
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        /* After unlocking, log the same address again to demonstrate no observable change. */
        fprintf(logf, "POST_AFTER_UNLOCK: address of lfs->cfg = %p\n", (void*)&lfs->cfg);
        fprintf(logf, "EXIT lfs_setattr -> %d\n\n", err);
        fclose(logf);
    }

    return err;
}
