int lfs_setattr(lfs_t *lfs, const char *path,
        uint8_t type, const void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe log: entry and state after lock */
    {
        FILE *f = fopen("/tmp/logs/88.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_setattr: lfs=%p path=\"%s\" type=%u buffer=%p size=%lu\n",
                    (void*)lfs, (path != NULL) ? path : "(null)", (unsigned)type, buffer, (unsigned long)size);
            fprintf(f, "after LFS_LOCK: err=%d cfg=%p\n", err, (void*)lfs->cfg);
            fclose(f);
        }
    }

    if (err) {
        FILE *f = fopen("/tmp/logs/88.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_setattr (lock failed): err=%d\n", err);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_setattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    err = lfs_setattr_(lfs, path, type, buffer, size);

    LFS_TRACE("lfs_setattr -> %d", err);

    /* Probe log: before unlocking */
    {
        FILE *f = fopen("/tmp/logs/88.txt", "a");
        if (f) {
            fprintf(f, "before LFS_UNLOCK: cfg=%p err=%d\n", (void*)lfs->cfg, err);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Probe log: after unlocking (to show no observable cfg change) */
    {
        FILE *f = fopen("/tmp/logs/88.txt", "a");
        if (f) {
            fprintf(f, "after LFS_UNLOCK: cfg=%p err=%d\n", (void*)lfs->cfg, err);
            fprintf(f, "EXIT lfs_setattr: returning %d\n", err);
            fclose(f);
        }
    }

    return err;
}
