int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);

    /* Logging probe: append control-flow/state info to /tmp/logs/559.txt */
    {
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "lfs_stat ENTER: lfs=%p path=\"%s\" info=%p\n",
                    (void*)lfs, (path != NULL) ? path : "(null)", (void*)info);
            fprintf(log, "After LFS_LOCK: err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Log the early return reason so the invariant (err == 0) can be observed */
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "lfs_stat EARLY RETURN: err=%d (lock indicated failure)\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_stat(%p, \"%s\", %p)", (void*)lfs, path, (void*)info);

    /* Log that we are about to call the real implementation */
    {
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "Calling lfs_stat_: lfs=%p path=\"%s\" info=%p\n",
                    (void*)lfs, (path != NULL) ? path : "(null)", (void*)info);
            fclose(log);
        }
    }

    err = lfs_stat_(lfs, path, info);

    /* Log the result from the underlying function */
    {
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "lfs_stat_ returned: %d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_stat -> %d", err);

    /* Log before unlock so we can see that unlock is reached in the normal path */
    {
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "About to LFS_UNLOCK on cfg=%p\n", (void*)lfs->cfg);
            fclose(log);
        }
    }

    LFS_UNLOCK(lfs->cfg);

    /* Final log of exit state */
    {
        FILE *log = fopen("/tmp/logs/559.txt", "a");
        if (log) {
            fprintf(log, "lfs_stat EXIT: returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
