int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file to record control-flow and state. If opening fails, continue
       without logging to avoid changing original behavior. */
    FILE *fp = fopen("/tmp/logs/552.txt", "a");
    if (fp) {
        /* Log entry and key state: pointer values, string contents (if any), and
           the address of the parameter 'path' so downstream comparisons can
           detect uses of &path. */
        fprintf(fp, "ENTER lfs_stat: lfs=%p, path_ptr=%p, path_str=%s, &path=%p, info=%p\n",
                (void *)lfs,
                (const void *)path,
                (path != NULL) ? path : "(null)",
                (void *)&path,
                (void *)info);
        fprintf(fp, "LFS_LOCK returned: %d\n", err);
        fflush(fp);
    }

    if (err) {
        if (fp) { fclose(fp); }
        return err;
    }

    LFS_TRACE("lfs_stat(%p, \"%s\", %p)", (void*)lfs, path, (void*)info);

    err = lfs_stat_(lfs, path, info);

    /* Log the result and a snapshot after calling lfs_stat_. This helps trace
       whether any deeper call modified objects that relate to the 'path'
       parameter (addresses can be compared with previous logs). */
    if (fp) {
        fprintf(fp, "AFTER lfs_stat_: err=%d, lfs=%p, path_ptr=%p, path_str=%s, &path=%p, info=%p\n",
                err,
                (void *)lfs,
                (const void *)path,
                (path != NULL) ? path : "(null)",
                (void *)&path,
                (void *)info);
        fprintf(fp, "RETURNING from lfs_stat: %d\n\n", err);
        fclose(fp);
    }

    LFS_TRACE("lfs_stat -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
