int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Probe: log entry state (value and address of the pointer parameter) */
    {
        FILE *f = fopen("/tmp/logs/552.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_stat: this=%p, path_value=%p, path_str=\"%s\", &path=%p, info=%p\n",
                    (void*)lfs, (const void*)path, path ? path : "(null)", (void*)&path, (void*)info);
            if (path) {
                fprintf(f, "path bytes (up to 32 or NUL):");
                for (int i = 0; i < 32 && path[i] != '\0'; i++) {
                    fprintf(f, " %02x", (unsigned char)path[i]);
                }
                fprintf(f, "\n");
            }
            fclose(f);
        }
    }

    LFS_TRACE("lfs_stat(%p, \"%s\", %p)", (void*)lfs, path ? path : "", (void*)info);

    /* Call the original implementation */
    err = lfs_stat_(lfs, path, info);

    /* Probe: log return state (capture again the pointer value and address) */
    {
        FILE *f = fopen("/tmp/logs/552.txt", "a");
        if (f) {
            fprintf(f, "AFTER lfs_stat_: err=%d, this=%p, path_value=%p, path_str=\"%s\", &path=%p, info=%p\n",
                    err, (void*)lfs, (const void*)path, path ? path : "(null)", (void*)&path, (void*)info);
            if (path) {
                fprintf(f, "path bytes after (up to 32 or NUL):");
                for (int i = 0; i < 32 && path[i] != '\0'; i++) {
                    fprintf(f, " %02x", (unsigned char)path[i]);
                }
                fprintf(f, "\n");
            }
            fclose(f);
        }
    }

    LFS_TRACE("lfs_stat -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
