int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    /*
     * We add lightweight file logging to /tmp/logs/558.txt to capture the
     * control flow and state around the LFS_UNLOCK usage so other agents can
     * reproduce the reported dead-code scenario.
     *
     * We deliberately keep this function's logic identical to the original
     * implementation: call LFS_LOCK, call lfs_stat_, call LFS_UNLOCK, and
     * return the result. The logging is purely observational.
     */

    int err = LFS_LOCK(lfs->cfg);

    /* Minimal forward declarations so we can log without including headers */
    struct FILE; typedef struct FILE FILE;
    extern FILE *fopen(const char *filename, const char *mode);
    extern int fprintf(FILE *stream, const char *format, ...);
    extern int fclose(FILE *stream);

    FILE *log = fopen("/tmp/logs/558.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_stat: lfs=%p path=\"%s\" info=%p\n",
                (void*)lfs, path ? path : "(null)", (void*)info);
        fprintf(log, "after LFS_LOCK: err=%d, lfs->cfg=%p\n",
                err, (void*)lfs->cfg);
    }

    if (err) {
        if (log) {
            fprintf(log, "EXIT lfs_stat (lock failed): err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_stat(%p, \"%s\", %p)", (void*)lfs, path, (void*)info);

    err = lfs_stat_(lfs, path, info);

    LFS_TRACE("lfs_stat -> %d", err);

    if (log) {
        /* Log the value passed to LFS_UNLOCK so observers can see it's only evaluated */
        fprintf(log, "about to call LFS_UNLOCK(lfs->cfg) with cfg=%p\n", (void*)lfs->cfg);
    }

    /* The original call: kept intact to preserve behavior. */
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        /* Log after the unlock call to demonstrate the call was executed. */
        fprintf(log, "after calling LFS_UNLOCK(lfs->cfg); returned from call, err=%d\n", err);
        fprintf(log, "EXIT lfs_stat: returning %d\n\n", err);
        fclose(log);
    }

    return err;
}
