int lfs_stat(lfs_t *lfs, const char *path, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Probe: log the entry point that would be produced by LFS_TRACE
       to /tmp/logs/558.txt so callers can observe the control flow and
       state even if LFS_TRACE is defined as an empty macro. */
    {
        FILE *f = fopen("/tmp/logs/558.txt", "a");
        if (f) {
            const char *path_str = path ? path : "(null)";
            fprintf(f, "LFS_TRACE: lfs_stat(%p, \"%s\", %p)\n",
                    (void*)lfs, path_str, (void*)info);
            fclose(f);
        }
    }

    err = lfs_stat_(lfs, path, info);

    /* Probe: log the result that would be produced by LFS_TRACE */
    {
        FILE *f = fopen("/tmp/logs/558.txt", "a");
        if (f) {
            fprintf(f, "LFS_TRACE: lfs_stat -> %d\n", err);
            fclose(f);
        }
    }

    LFS_UNLOCK(lfs->cfg);
    return err;
}
