int32 OS_API_Impl_Init(osal_objtype_t idtype)
{
    int32 return_code;
    FILE *logf = NULL;

    /* Try to open primary log file; if not available, fall back to /tmp/3078.txt */
    logf = fopen("/tmp/logs/3078.txt", "a");
    if (logf == NULL)
    {
        logf = fopen("/tmp/3078.txt", "a");
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Impl_Init: enter, idtype=%d\n", (int)idtype);
    }

    return_code = OS_Posix_TableMutex_Init(idtype);
    if (logf != NULL)
    {
        fprintf(logf, "OS_Posix_TableMutex_Init returned %ld\n", (long)return_code);
    }

    if (return_code != OS_SUCCESS)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Impl_Init: early return (TableMutex init failed) return_code=%ld\n", (long)return_code);
            fclose(logf);
        }
        return return_code;
    }

    switch (idtype)
    {
        case OS_OBJECT_TYPE_OS_TASK:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_TASK -> calling OS_Posix_TaskAPI_Impl_Init()\n");
            return_code = OS_Posix_TaskAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_TaskAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_QUEUE:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_QUEUE -> calling OS_Posix_QueueAPI_Impl_Init()\n");
            return_code = OS_Posix_QueueAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_QueueAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_BINSEM:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_BINSEM -> calling OS_Posix_BinSemAPI_Impl_Init()\n");
            return_code = OS_Posix_BinSemAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_BinSemAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_COUNTSEM:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_COUNTSEM -> calling OS_Posix_CountSemAPI_Impl_Init()\n");
            return_code = OS_Posix_CountSemAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_CountSemAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_MUTEX:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_MUTEX -> calling OS_Posix_MutexAPI_Impl_Init()\n");
            return_code = OS_Posix_MutexAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_MutexAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_MODULE:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_MODULE -> calling OS_Posix_ModuleAPI_Impl_Init()\n");
            return_code = OS_Posix_ModuleAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_ModuleAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_TIMEBASE:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_TIMEBASE -> calling OS_Posix_TimeBaseAPI_Impl_Init()\n");
            return_code = OS_Posix_TimeBaseAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_TimeBaseAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_STREAM:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_STREAM -> calling OS_Posix_StreamAPI_Impl_Init()\n");
            return_code = OS_Posix_StreamAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_StreamAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_DIR:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_DIR -> calling OS_Posix_DirAPI_Impl_Init()\n");
            return_code = OS_Posix_DirAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_DirAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        case OS_OBJECT_TYPE_OS_FILESYS:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_FILESYS -> calling OS_Posix_FileSysAPI_Impl_Init()\n");
            return_code = OS_Posix_FileSysAPI_Impl_Init();
            if (logf != NULL)
            {
                fprintf(logf, "OS_Posix_FileSysAPI_Impl_Init returned %ld\n", (long)return_code);
                if (return_code == OS_SUCCESS)
                {
                    fprintf(logf, "Note: OS_Posix_FileSysAPI_Impl_Init returned OS_SUCCESS (possible no-op implementation)\n");
                }
            }
            break;
        case OS_OBJECT_TYPE_OS_CONDVAR:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: case OS_CONDVAR -> calling OS_Posix_CondVarAPI_Impl_Init()\n");
            return_code = OS_Posix_CondVarAPI_Impl_Init();
            if (logf != NULL) fprintf(logf, "OS_Posix_CondVarAPI_Impl_Init returned %ld\n", (long)return_code);
            break;
        default:
            if (logf != NULL) fprintf(logf, "OS_API_Impl_Init: default case (no action)\n");
            break;
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Impl_Init: exit, returning %ld for idtype=%d\n", (long)return_code, (int)idtype);
        fclose(logf);
    }

    return return_code;
}
