int32 OS_API_Init(void)
{
    int32          return_code = OS_SUCCESS;
    osal_objtype_t idtype;
    uint32         microSecPerSec;

    /* Logging probe: attempt to log to file(s). Keep local FILE* so we do not affect global state. */
    FILE *logf = fopen("/tmp/logs/798.txt", "a");
    if (logf == NULL)
    {
        /* Fallback to a location that is more likely to exist */
        logf = fopen("/tmp/798.txt", "a");
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): entered\n");
        fprintf(logf, "  Initial GlobalState = 0x%lx\n", (unsigned long)OS_SharedGlobalVars.GlobalState);
        fflush(logf);
    }

    /*
     * If OSAL is already initialized, not really a big issue, just return.
     * This is not typically expected though, so its worth a debug statement.
     *
     * However this can validly occur when running tests on some platforms
     * without a reset/reload between invocations.
     */
    if (OS_SharedGlobalVars.GlobalState == OS_INIT_MAGIC_NUMBER)
    {
        /* Record that this path was taken and capture the fact that OS_DEBUG would have been invoked here. */
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): redundant init detected - would have called OS_DEBUG but macro may be empty. Returning OS_SUCCESS.\n");
            fflush(logf);
            fclose(logf);
        }
        return OS_SUCCESS;
    }

    /* Wipe global state structure to be sure everything is clean */
    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): wiping OS_SharedGlobalVars (memset)\n");
        fflush(logf);
    }
    memset(&OS_SharedGlobalVars, 0, sizeof(OS_SharedGlobalVars));

    /* Reset debug to default level if enabled */
#if defined(OSAL_CONFIG_DEBUG_PRINTF)
    OS_SharedGlobalVars.DebugLevel = 1;
    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): OSAL_CONFIG_DEBUG_PRINTF defined - DebugLevel set to 1\n");
        fflush(logf);
    }
#endif

    /* Set flag that says OSAL has been initialized */
    OS_SharedGlobalVars.GlobalState = OS_INIT_MAGIC_NUMBER;
    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): GlobalState set to OS_INIT_MAGIC_NUMBER (0x%lx)\n", (unsigned long)OS_SharedGlobalVars.GlobalState);
        fflush(logf);
    }

    /* Initialize the common table that everything shares */
    return_code = OS_ObjectIdInit();
    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): OS_ObjectIdInit() returned %d\n", (int)return_code);
        fflush(logf);
    }
    if (return_code != OS_SUCCESS)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): aborting early due to OS_ObjectIdInit failure (%d)\n", (int)return_code);
            fclose(logf);
        }
        return return_code;
    }

    for (idtype = 0; idtype < OS_OBJECT_TYPE_USER; ++idtype)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): initializing idtype 0x%x\n", (unsigned int)idtype);
            fflush(logf);
        }

        /* Initialize the implementation first, as the shared layer depends on it */
        return_code = OS_API_Impl_Init(idtype);
        if (logf != NULL)
        {
            fprintf(logf, "  OS_API_Impl_Init(0x%x) returned %d\n", (unsigned int)idtype, (int)return_code);
            fflush(logf);
        }
        if (return_code != OS_SUCCESS)
        {
            if (logf != NULL)
            {
                fprintf(logf, "  OS_API_Impl_Init(0x%x) failed to initialize: %d\n", (unsigned int)idtype, (int)return_code);
                fflush(logf);
            }
            break;
        }

        switch (idtype)
        {
            case OS_OBJECT_TYPE_OS_TASK:
                return_code = OS_TaskAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_QUEUE:
                return_code = OS_QueueAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_BINSEM:
                return_code = OS_BinSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_COUNTSEM:
                return_code = OS_CountSemAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MUTEX:
                return_code = OS_MutexAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_MODULE:
                return_code = OS_ModuleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMEBASE:
                return_code = OS_TimeBaseAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_TIMECB:
                return_code = OS_TimerCbAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_STREAM:
                return_code = OS_FileAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_DIR:
                return_code = OS_DirAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_FILESYS:
                return_code = OS_FileSysAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONSOLE:
                return_code = OS_ConsoleAPI_Init();
                break;
            case OS_OBJECT_TYPE_OS_CONDVAR:
                return_code = OS_CondVarAPI_Init();
                break;
            default:
                break;
        }

        if (logf != NULL)
        {
            fprintf(logf, "  idtype 0x%x shared init returned %d\n", (unsigned int)idtype, (int)return_code);
            fflush(logf);
        }
        if (return_code != OS_SUCCESS)
        {
            if (logf != NULL)
            {
                fprintf(logf, "ID type 0x%x shared layer failed to initialize: %d\n", (unsigned int)idtype, (int)return_code);
                fflush(logf);
            }
            break;
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): calling OS_NetworkAPI_Init()\n");
            fflush(logf);
        }
        return_code = OS_NetworkAPI_Init();
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): OS_NetworkAPI_Init() returned %d\n", (int)return_code);
            fflush(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): calling OS_SocketAPI_Init()\n");
            fflush(logf);
        }
        return_code = OS_SocketAPI_Init();
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): OS_SocketAPI_Init() returned %d\n", (int)return_code);
            fflush(logf);
        }
    }

    /*
     * Confirm that somewhere during initialization,
     * the time variables got set to something valid
     */
    if (return_code == OS_SUCCESS &&
        (OS_SharedGlobalVars.MicroSecPerTick == 0 || OS_SharedGlobalVars.TicksPerSecond == 0))
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): Implementation failed to initialize tick time globals (MicroSecPerTick=%ld, TicksPerSecond=%ld)\n",
                    (long)OS_SharedGlobalVars.MicroSecPerTick, (long)OS_SharedGlobalVars.TicksPerSecond);
            fflush(logf);
        }
        return_code = OS_ERROR;
    }

    microSecPerSec = OS_SharedGlobalVars.MicroSecPerTick * OS_SharedGlobalVars.TicksPerSecond;

    if (microSecPerSec != 1000000)
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): Warning: Microsecs per sec value of %lu does not equal 1000000 (MicroSecPerTick: %ld   TicksPerSecond: %ld)\n",
                    (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
                    (long)OS_SharedGlobalVars.TicksPerSecond);
            fflush(logf);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        /*
         * Some part of init failed, so set global flag that says OSAL is in shutdown state.
         *
         * In particular if certain internal resources (such as the console utility task)
         * were created, this should cause those tasks to self-exit such that the system
         * is ultimately returned to the same state it started in.
         */
        if (logf != NULL)
        {
            fprintf(logf, "OS_API_Init(): init failed with %d - setting GlobalState to OS_SHUTDOWN_MAGIC_NUMBER (0x%lx)\n",
                    (int)return_code, (unsigned long)OS_SHUTDOWN_MAGIC_NUMBER);
            fflush(logf);
        }
        OS_SharedGlobalVars.GlobalState = OS_SHUTDOWN_MAGIC_NUMBER;
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_API_Init(): exiting with return_code=%d\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
