void OS_API_Teardown(void)
{
    /*
     * This should delete any remaining user-created objects/tasks
     * Preserve original behavior while adding logging probes to aid reproduction
     * of the reported string-handling violation scenario. The logs are appended
     * to /tmp/logs/643.txt so test harnesses can inspect control flow.
     */
    FILE *log = NULL;

    /* Try to open the log file; if not available, continue silently. */
    log = fopen("/tmp/logs/643.txt", "a");
    if (log != NULL)
    {
        /* Log entry and the addresses of the functions being invoked for traceability */
        fprintf(log, "ENTER: OS_API_Teardown()\n");
        fprintf(log, "TRACE: OS_API_Teardown addr=%p\n", (void *)OS_API_Teardown);
        fprintf(log, "TRACE: OS_DeleteAllObjects addr=%p\n", (void *)OS_DeleteAllObjects);
        fprintf(log, "TRACE: OS_ApplicationShutdown addr=%p\n", (void *)OS_ApplicationShutdown);
        fprintf(log, "ACTION: Calling OS_DeleteAllObjects()\n");
        fflush(log);
    }

    /* Original behavior retained */
    OS_DeleteAllObjects();

    if (log != NULL)
    {
        fprintf(log, "RETURN: OS_DeleteAllObjects() completed\n");
        fprintf(log, "ACTION: Calling OS_ApplicationShutdown(true)\n");
        fflush(log);
    }

    /* This should cause the "internal" objects (e.g. console utility task)
     * to exit, and will prevent any new objects from being created.
     */
    OS_ApplicationShutdown(true);

    if (log != NULL)
    {
        fprintf(log, "RETURN: OS_ApplicationShutdown(true) completed\n");
        fprintf(log, "EXIT: OS_API_Teardown()\n");
        fclose(log);
    }
}
