        fflush(log);
    }

    /* Preserve original behavior: board support package setup */
    UT_BSP_Setup();

    if (log)
    {
        fprintf(log, "Returned from: UT_BSP_Setup()\n");
        fprintf(log, "Beginning SETUP segment via UtAssert_BeginTest(\"SETUP\")\n");
        fflush(log);
    }

    /*
     * Wrap the UtTest_Setup() function in a UT segment called "SETUP"
     * This allows any assert calls to be used and recorded during setup
     */
    UtAssert_BeginTest("SETUP");

    if (log)
    {
        fprintf(log, "Entered SETUP segment. About to call: UtTest_Setup()\n");
        fflush(log);
    }

    UtTest_Setup();

    if (log)
    {
        fprintf(log, "Returned from: UtTest_Setup()\n");
        fprintf(log, "Ending SETUP segment via UtAssert_EndTest()\n");
        fflush(log);
    }

    UtAssert_EndTest();

    if (log)
    {
        /*
