void OS_Application_Startup(void)
{
    FILE *log = fopen("/tmp/logs/693.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_Application_Startup\n");
        /* Record addresses of the key functions called in this startup path so that
         * an external investigator can correlate behavior and control flow with
         * other runtime evidence (e.g. memory maps, crash traces). */
        fprintf(log, "UtTest_EarlyInit addr=%p\n", (void *)UtTest_EarlyInit);
        fprintf(log, "UT_BSP_Setup addr=%p\n", (void *)UT_BSP_Setup);
        fprintf(log, "UtAssert_BeginTest addr=%p\n", (void *)UtAssert_BeginTest);
        fprintf(log, "UtTest_Setup addr=%p\n", (void *)UtTest_Setup);
        fprintf(log, "UtAssert_EndTest addr=%p\n", (void *)UtAssert_EndTest);
        fflush(log);
    }

    /* Preserve original behavior: initialize test framework and BSP */
    UtTest_EarlyInit();
    if (log != NULL)
    {
        fprintf(log, "After UtTest_EarlyInit\n");
        fflush(log);
    }

    UT_BSP_Setup();
    if (log != NULL)
    {
        fprintf(log, "After UT_BSP_Setup\n");
        fflush(log);
    }

    /* Wrap the setup in the same UT segment as before; log before/after to
     * make it easy to determine whether any subsequent crash or misbehavior
     * happened during setup. */
    UtAssert_BeginTest("SETUP");
    if (log != NULL)
    {
        fprintf(log, "After UtAssert_BeginTest (entered SETUP)\n");
        fflush(log);
    }

    UtTest_Setup();
    if (log != NULL)
    {
        fprintf(log, "After UtTest_Setup\n");
        fflush(log);
    }

    UtAssert_EndTest();
    if (log != NULL)
    {
        fprintf(log, "After UtAssert_EndTest (exited SETUP)\n");
        fclose(log);
    }
}
