void OS_Application_Startup(void)
{
    /* Minimal logging/probing to help reproduce and capture the reported rule 21.17 scenario. */
    FILE *log = NULL;

    /* Try the primary logging path. If that fails, fall back to a safe /tmp path. */
    log = fopen("/tmp/logs/693.txt", "a");
    if (log == NULL)
    {
        /* Fallback: write to /tmp/693.txt if the logs directory doesn't exist or is not writable. */
        log = fopen("/tmp/693.txt", "a");
    }

    if (log != NULL)
    {
        fprintf(log, "OS_Application_Startup: ENTER\n");
        /* Record presence of key setup functions (addresses shown for extra traceability). */
        fprintf(log, "Probes: UtTest_EarlyInit=%p UT_BSP_Setup=%p UtTest_Setup=%p UtAssert_BeginTest=%p UtAssert_EndTest=%p\n",
                (void *)UtTest_EarlyInit,
                (void *)UT_BSP_Setup,
                (void *)UtTest_Setup,
                (void *)UtAssert_BeginTest,
                (void *)UtAssert_EndTest);
        fflush(log);
    }

    /* Preserve original behavior: early initialization */
    UtTest_EarlyInit();
    if (log != NULL) { fprintf(log, "Called: UtTest_EarlyInit()\n"); fflush(log); }

    /* Preserve original behavior: BSP setup */
    UT_BSP_Setup();
    if (log != NULL) { fprintf(log, "Called: UT_BSP_Setup()\n"); fflush(log); }

    /* Wrap the UtTest_Setup() function in a UT segment called "SETUP" */
    if (log != NULL) { fprintf(log, "About to call: UtAssert_BeginTest(\"SETUP\")\n"); fflush(log); }
    UtAssert_BeginTest("SETUP");
    if (log != NULL) { fprintf(log, "Called: UtAssert_BeginTest(\"SETUP\")\n"); fflush(log); }

    /* Call the original setup function */
    UtTest_Setup();
    if (log != NULL) { fprintf(log, "Called: UtTest_Setup()\n"); fflush(log); }

    UtAssert_EndTest();
    if (log != NULL) { fprintf(log, "Called: UtAssert_EndTest()\n"); fflush(log); }

    if (log != NULL)
    {
        fprintf(log, "OS_Application_Startup: EXIT\n\n");
        fclose(log);
    }
}
