void OS_BSP_ConsoleOutput_Impl(const char *Str, size_t DataLen)
{
    ssize_t WriteLen;
    int logfd = -1;
    char buf[256];
    ssize_t n;

    /* Ensure log directory exists (ignore errors) */
    (void)mkdir("/tmp/logs", 0755);

    /* Open log file for append so multiple runs accumulate evidence */
    logfd = open("/tmp/logs/1412.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);

    if (logfd >= 0)
    {
        n = snprintf(buf, sizeof(buf), "OS_BSP_ConsoleOutput_Impl entered: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);
        if (n > 0) (void)write(logfd, buf, (size_t)n);
    }

    while (DataLen > 0)
    {
        /* log about to perform write */
        if (logfd >= 0)
        {
            n = snprintf(buf, sizeof(buf), "About to write: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
        }

        /* writes the raw data directly to STDOUT_FILENO (unbuffered) */
        WriteLen = write(STDOUT_FILENO, Str, DataLen);

        if (logfd >= 0)
        {
            n = snprintf(buf, sizeof(buf), "write returned: WriteLen=%zd\n", WriteLen);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
        }

        if (WriteLen <= 0)
        {
            /* no recourse if this fails, just stop. */
            if (logfd >= 0)
            {
                n = snprintf(buf, sizeof(buf), "Write failed or zero, breaking. WriteLen=%zd\n", WriteLen);
                if (n > 0) (void)write(logfd, buf, (size_t)n);
                (void)close(logfd);
                logfd = -1;
            }
            break;
        }

        /* Log state before modifying the pointer parameter */
        if (logfd >= 0)
        {
            n = snprintf(buf, sizeof(buf), "Before modify: Str=%p DataLen=%zu WriteLen=%zd\n", (const void *)Str, DataLen, WriteLen);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
        }

        /* --- Violation scenario: modifying the pointer parameter 'Str' --- */
        Str += WriteLen;
        DataLen -= WriteLen;

        /* Log state after modification so reviewers can observe the pointer change */
        if (logfd >= 0)
        {
            n = snprintf(buf, sizeof(buf), "After modify: Str=%p DataLen=%zu\n", (const void *)Str, DataLen);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
        }
    }

    if (logfd >= 0) (void)close(logfd);
}
