void OS_BSP_Initialize(void)
{
    FILE *              fp;
    FILE *              logfp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /* Open probe log file to capture control flow and state for analysis */
    logfp = fopen("/tmp/logs/2509.txt", "a");
    if (logfp)
    {
        fprintf(logfp, "OS_BSP_Initialize: entered\n");
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     * (Preserve original behavior; add logs to record decisions and values.)
     */
    if (geteuid() != 0)
    {
        if (logfp) fprintf(logfp, "geteuid() != 0 -> checking /proc/sys/fs/mqueue/msg_max\n");
        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                unsigned long parsed = strtoul(buffer, NULL, 10);
                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(parsed);
                if (logfp)
                {
                    fprintf(logfp,
                            "Read /proc/sys/fs/mqueue/msg_max: raw=\"%s\" parsed=%lu MaxQueueDepth=%u\n",
                            buffer, parsed, (unsigned int)OS_BSP_Global.MaxQueueDepth);
                }

                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
            }
            else
            {
                if (logfp) fprintf(logfp, "fgets returned NULL when reading /proc/sys/fs/mqueue/msg_max\n");
            }
            fclose(fp);
        }
        else
        {
            if (logfp) fprintf(logfp, "Could not open /proc/sys/fs/mqueue/msg_max\n");
        }
    }
    else
    {
        if (logfp) fprintf(logfp, "geteuid() == 0 -> running as root; skipping /proc check\n");
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    if (logfp) fprintf(logfp, "Calling pthread_mutexattr_init()\n");
    status = pthread_mutexattr_init(&mutex_attr);
    if (logfp) fprintf(logfp, "pthread_mutexattr_init returned %d\n", status);
    if (status < 0)
    {
        /* Preserve original debug call */
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));

        if (logfp)
        {
            /* Defensive: report numeric status and strerror where sensible */
            fprintf(logfp, "pthread_mutexattr_init error branch taken: status=%d\n", status);
            if (status >= 0)
            {
                fprintf(logfp, "pthread_mutexattr_init strerror: %s\n", strerror(status));
            }
        }
    }

    if (logfp) fprintf(logfp, "Calling pthread_mutexattr_setprotocol(PTHREAD_PRIO_INHERIT)\n");
    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (logfp) fprintf(logfp, "pthread_mutexattr_setprotocol returned %d\n", status);
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
        if (logfp)
        {
            fprintf(logfp, "pthread_mutexattr_setprotocol error branch taken: status=%d\n", status);
            if (status >= 0)
            {
                fprintf(logfp, "pthread_mutexattr_setprotocol strerror: %s\n", strerror(status));
            }
        }
    }
    else
    {
        /* Record the fact that the original negative-status branch is not taken. This helps
         * demonstrate the reported invariant: POSIX pthread functions return 0 on success or
         * a positive error number on failure, so tests for status < 0 are never taken. */
        if (logfp)
        {
            fprintf(logfp,
                    "pthread_mutexattr_setprotocol did not return negative. Observed status=%d (0 means success; >0 indicates error code)\n",
                    status);
        }
    }

    if (logfp) fprintf(logfp, "Calling pthread_mutex_init()\n");
    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (logfp) fprintf(logfp, "pthread_mutex_init returned %d\n", status);
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
        if (logfp)
        {
            fprintf(logfp, "pthread_mutex_init error branch taken: status=%d\n", status);
            if (status >= 0)
            {
                fprintf(logfp, "pthread_mutex_init strerror: %s\n", strerror(status));
            }
        }
    }

    if (logfp)
    {
        fprintf(logfp, "OS_BSP_Initialize: exiting\n");
        fflush(logfp);
        fclose(logfp);
    }
}
