void OS_BSP_Initialize(void)
{
    FILE *              fp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;
    FILE *              log_fp = NULL;

    /* Open persistent log for collecting probe information */
    log_fp = fopen("/tmp/logs/2506.txt", "a");
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: ENTER\n");
        fflush(log_fp);
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: geteuid() != 0, checking /proc/sys/fs/mqueue/msg_max\n");
            fflush(log_fp);
        }

        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(strtoul(buffer, NULL, 10));
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
                if (log_fp)
                {
                    fprintf(log_fp, "OS_BSP_Initialize: read msg_max='%s' -> MaxQueueDepth=%u\n",
                            buffer, (unsigned int)OS_BSP_Global.MaxQueueDepth);
                    fflush(log_fp);
                }
            }
            else
            {
                if (log_fp)
                {
                    fprintf(log_fp, "OS_BSP_Initialize: fgets returned NULL while reading msg_max\n");
                    fflush(log_fp);
                }
            }
            fclose(fp);
        }
        else
        {
            if (log_fp)
            {
                fprintf(log_fp, "OS_BSP_Initialize: could not open /proc/sys/fs/mqueue/msg_max (not on Linux or no permission)\n");
                fflush(log_fp);
            }
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: running as root, skipping msg_max check\n");
            fflush(log_fp);
        }
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    status = pthread_mutexattr_init(&mutex_attr);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutexattr_init returned status=%d\n", status);
        fflush(log_fp);
    }
    if (status < 0)
    {
        /* Original diagnostic preserved */
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: ERROR branch taken: pthread_mutexattr_init: %s\n", strerror(status));
            fflush(log_fp);
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: NOTE: pthread_mutexattr_init did not take the <0 branch (status=%d)\n", status);
            fflush(log_fp);
        }
    }

    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutexattr_setprotocol returned status=%d\n", status);
        fflush(log_fp);
    }
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: ERROR branch taken: pthread_mutexattr_setprotocol: %s\n", strerror(status));
            fflush(log_fp);
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: NOTE: pthread_mutexattr_setprotocol did not take the <0 branch (status=%d)\n", status);
            fflush(log_fp);
        }
    }

    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutex_init returned status=%d\n", status);
        fflush(log_fp);
    }
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: ERROR branch taken: pthread_mutex_init: %s\n", strerror(status));
            fflush(log_fp);
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: NOTE: pthread_mutex_init did not take the <0 branch (status=%d)\n", status);
            fflush(log_fp);
        }
    }

    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: EXIT\n");
        fclose(log_fp);
    }
}
